mapper.select:

	   SELECT 	ID, USER_ID, USER_PW, USER_NAME, USER_EMAIL, USER_AUTHOR
   FROM		MEMBERS
   WHERE ID= #{ID}

mapper.insert:
     	 INSERT INTO MEMBERS
				( ID
				  , USER_ID
				  , USER_PW
				  , USER_NAME
				  , USER_EMAIL
				  , USER_AUTHOR
				  , REG_DATE )
			VALUES ( (
						SELECT MAX(ID)+1
						FROM MEMBERS
					)
				  , #{user_id}
				  , #{user_pw}
				  , #{user_name}
				  , #{user_email}
				  , 'user'
				  , sysdate
				  )
